definition.script:
node {
            stage 'Build image and deploy in Dev'
            echo 'Building docker image and deploying to Dev'
            buildTicketMonster('dev1','development')

            stage 'Wait for approval'
            input 'Approve to testing?'

            stage 'Deploy to testing'
            echo 'Deploying to testing'
            deployTicketMonster('test1', 'development', 'testing')

            stage 'Wait for approval'
            input 'Approve to production?'

            stage 'Deploy to production'
            echo 'Deploying to prodution'
            deployTicketMonster('test1', 'testing', 'production')

            }

            // Creates a Build and triggers it
            def buildTicketMonster(String user, String project){
            projectSet(user, project)
            sh "oc new-build --binary --name=eap-app -l app=eap-app || echo 'Build exists'"
            sh "oc start-build eap-app"
            appDeploy()
            }

            // Tag the ImageStream from an original project to force a deployment
            def deployTicketMonster(String user, String origProject, String project){
            //sh "oc policy add-role-to-user system:image-puller system:serviceaccount:${project}:default -n ${origProject}"
            projectSet(user, project)
            sh "oc tag ${origProject}/eap-app:latest ${project}/eap-app:latest"
            appDeploy()
            }

            // Login and set the project
            def projectSet(String user, String project){
            // Use a credential called openshift-dev
            // withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'dev1', usernameVariable: 'USERNAME', passwordVariable: 'PASSWORD']]) {
            // sh "oc login --insecure-skip-tls-verify=true -u $env.USERNAME -p $env.PASSWORD https://10.1.2.2:8443"
            sh "oc login --insecure-skip-tls-verify=true -u ${user} -p ${user} https://ose-master.example.com:8443"
            //}
            sh "oc new-project ${project} || echo 'Project exists'"
            sh "oc project ${project}"
            }

            // Deploy the project based on a existing ImageStream
            def appDeploy(){
            sh "oc new-app development/eap-app:promote || echo 'Aplication already Exists'"
            sh "oc expose svc eap-app || echo 'Service already exposed'"
            //    sh "oc new-app aloha -l app=aloha,hystrix.enabled=true || echo 'Aplication already Exists'"
            //    sh "oc expose service aloha || echo 'Service already exposed'"
            //    sh 'oc patch dc/aloha -p \'{"spec":{"template":{"spec":{"containers":[{"name":"aloha","ports":[{"containerPort": 8778,"name":"jolokia"}]}]}}}}\''
            //    sh 'oc patch dc/aloha -p \'{"spec":{"template":{"spec":{"containers":[{"name":"aloha","readinessProbe":{"httpGet":{"path":"/api/health","port":8080}}}]}}}}\''
            }
